forever(function () {
    if (currentLevel == 0) {
        intro()
        currentLevel = 1
        previousLevel = 0
    } else if (currentLevel == 1) {
        level1()
        previousLevel = 1
    } else if (currentLevel == 2) {
        level2()
        previousLevel = 2
    } else if (currentLevel == 3) {
        level3()
        previousLevel = 3
    } else {
        gameover()
    }
})
